xkcdLabel[{str_String, {x1_, y1_}, {xo_, yo_}}] := Module[{x2, y2},
  x2 = x1 + xo; y2 = y1 + yo;
  {
    Inset[
      Style[str, xkcdStyle],
      {x2, y2},
      {1.2*Sign[x1 - x2], Sign[y1 - y2] Boole[x1 == x2]}
    ],
    Thick,
